/**
 * The configuration options for the RadialGauge pointer cap.
 */
export interface Cap {
    /**
     * The color of the cap.
     * Accepts valid CSS color strings, including hex and rgb.
     */
    color?: string;
    /**
     * The size of the cap in percent (from 0 to 1).
     */
    size?: number;
}
